INSERT INTO `localized_command` (`Language`, `Command`, `Text`) VALUES ("enUS", "maffiagame/basecommand/lynch", "\u0002{0}: Kit akarsz lincselni?\u000f\n\u0002{0}: A lincselési lehetőség ki van kapcsolva!\u000f\n\u0002{0}: Kérlek maradj csendben amíg a játék véget ér.\u000f\n\u0002{0}: Csak nappal lehet lincselni!\u000f\n\u0002{0}: Ilyen játékos nincs. Kérlek válasz mást!\u000f\n\u0002{0}: Ő már halott. Szavazz másra!\u000f\n\u0002{0}: Önmagadat lincselnéd meg? Normális vagy?\u000f\n\u0002{0}: Már szavaztál rá!\u000f\n\u0002{0} arra szavazott, hogy {1} legyen meglincselve!\u000f\nszavazat\n\u0002{0} szavazat kell a többséghez. Jelenlegi szavazatok:{1}\u000f\n\u0002A többség \u00034{0}\u000f\u000f\u0002 lincselése mellett döntött! Elszabadulnak az indulatok. Ő mostantól már halott.\u000f\n\u0002Megölték a főnököt! Szemetek!!!\u000f\n\u0002({0} meghalt, és nem szólhat hozzá a játékhoz.)\u000f");
