INSERT INTO `localized_command` (`Language`, `Command`, `Text`) VALUES ("enUS", "maffiagame/basecommand/see", "\u0002Kit akarsz kikérdezni?\u000f\n\u0002Csak este nyomozhatsz!\u000f\n\u0002Nem vagy nyomozó!\u000f\n\u0002Ma este már kikérdeztél valakit!\u000f\n\u0002Ő már halott. Válasz mást!\u000f\n\u0002Kit akarsz kikérdezni?\u000f\n\u0002Önmagadat akarod kikérdezni? Te tudod :P\u000f\n\u0002A jelentést reggel kapod meg!\u000f");
